package com.farmtrak.repository;

import com.farmtrak.model.VerificationToken;
import org.springframework.data.jpa.repository.JpaRepository;
import org.springframework.data.jpa.repository.Query;
import org.springframework.data.repository.query.Param;

import java.time.Instant;
import java.util.Optional;

public interface VerificationTokenRepository extends JpaRepository<VerificationToken, Long> {

    @Query("SELECT t FROM VerificationToken t WHERE t.email = :email AND t.code = :code AND t.expiresAt > :now AND t.used = false")
    Optional<VerificationToken> findValidToken(@Param("email") String email, @Param("code") String code, @Param("now") Instant now);
}

package com.farmtrak.service;

import com.farmtrak.model.OTP;
import com.farmtrak.repository.OTPRepository;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.stereotype.Service;

import java.time.LocalDateTime;
import java.util.Random;

@Service
public class AuthService {

    @Autowired
    private OTPRepository otpRepository;

    @Autowired
    private EmailService emailService;

    private static final int OTP_LENGTH = 6;
    private static final int OTP_EXPIRATION_MINUTES = 10;

    public void sendVerification(String email, String username) {
        if (email == null || email.isEmpty() || username == null || username.isEmpty()) {
            throw new IllegalArgumentException("Email and username must be provided");
        }

        String code = generateNumericOTP();
        LocalDateTime expiry = LocalDateTime.now().plusMinutes(OTP_EXPIRATION_MINUTES);

        OTP otp = new OTP(email, code, expiry, false);
        otpRepository.save(otp);

        // Send real email
        emailService.sendVerificationEmail(email, username, code);
    }

    public boolean verifyEmail(String email, String code) {
        OTP otp = otpRepository.findByEmailAndCode(email, code);

        if (otp != null && !otp.isVerified() && otp.getExpiryTime().isAfter(LocalDateTime.now())) {
            otp.setVerified(true);
            otpRepository.save(otp);
            return true;
        }
        return false;
    }

    private String generateNumericOTP() {
        Random random = new Random();
        StringBuilder otp = new StringBuilder();
        for (int i = 0; i < OTP_LENGTH; i++) {
            otp.append(random.nextInt(10));
        }
        return otp.toString();
    }
}

package com.farmtrak.service;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.mail.SimpleMailMessage;
import org.springframework.mail.javamail.JavaMailSender;
import org.springframework.stereotype.Service;

@Service
public class EmailService {

    @Autowired
    private JavaMailSender mailSender;

    /**
     * Sends a verification email with the OTP.
     * Throws RuntimeException if email sending fails.
     */
    public void sendVerificationEmail(String toEmail, String username, String verifyCode) {
        try {
            SimpleMailMessage message = new SimpleMailMessage();
            message.setTo(toEmail);
            message.setSubject("Your 6-Digit Verification Code");

            String textContent = "Hello, " + username + "\n\n"
                    + "Your verification code is: " + verifyCode + "\n\n"
                    + "Please enter this code to verify your email address.\n\n"
                    + "Thank you,\nFarmTrak Team";

            message.setText(textContent);

            mailSender.send(message);
        } catch (Exception e) {
            System.err.println("Failed to send verification email to " + toEmail + ": " + e.getMessage());
            throw new RuntimeException("Failed to send verification email");
        }
    }
}

