// src/pages/Welcome.jsx
import { useState, useEffect } from "react";
import { MoonIcon, SunIcon } from "@heroicons/react/24/outline";
import { useNavigate } from "react-router-dom";
import { logOut, isLoggedIn, logIn } from "../utils/login";
import LoginModal from "../components/LoginModal";

function Welcome() {
  const [isDarkMode, setIsDarkMode] = useState(() => localStorage.getItem("theme") === "dark");
  const [showLogin, setShowLogin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    if (isLoggedIn()) navigate("/dashboard");
  }, [navigate]);

  useEffect(() => {
    const root = document.documentElement;
    if (isDarkMode) {
      root.classList.add("dark");
      localStorage.setItem("theme", "dark");
    } else {
      root.classList.remove("dark");
      localStorage.setItem("theme", "light");
    }
  }, [isDarkMode]);

  function handleLogout() {
    logOut();
    navigate("/");
  }

  const handleLoginSuccess = ({ token, email }) => {
    logIn(token, email);
    setShowLogin(false);
    navigate("/dashboard");
  };

  return (
    <div className="relative bg-light-bg dark:bg-dark-bg text-light-text dark:text-dark-text min-h-screen">
      {/* Top-right controls */}
      <div className="absolute top-4 right-8 flex items-center gap-3 z-10">
        <button
          onClick={() => setIsDarkMode(!isDarkMode)}
          className="p-2 rounded-full hover:bg-light-muted dark:hover:bg-dark-dim transition"
          aria-label="Toggle theme"
        >
          {isDarkMode ? (
            <SunIcon className="h-6 w-6 text-yellow-400" />
          ) : (
            <MoonIcon className="h-6 w-6 text-gray-700" />
          )}
        </button>

        {isLoggedIn() ? (
          <button
            onClick={handleLogout}
            aria-label="Logout"
            className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
          >
            🔓 Logout
          </button>
        ) : (
          <button
            onClick={() => setShowLogin(true)}
            aria-label="Login"
            className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-500"
          >
            🔐 Login
          </button>
        )}
      </div>

      {/* Hero Section */}
      <div className="flex flex-col justify-center items-center min-h-screen text-center px-4">
        <h1 className="text-5xl font-semibold sm:text-7xl transition-transform duration-500 hover:scale-105">
          Welcome to <span className="text-light-primary dark:text-dark-primary">FarmTrak</span>
        </h1>
        <p className="mt-6 text-lg text-light-subtext dark:text-dark-subtext max-w-2xl">
          Manage your poultry with ease. Track flocks, expenses, feed, and more.
        </p>
        <div className="mt-8 flex justify-center gap-x-6">
          <button
            onClick={() => {
              if (isLoggedIn()) navigate("/dashboard");
              else setShowLogin(true);
            }}
            className="bg-light-primary hover:bg-light-primaryHover dark:bg-dark-primary dark:hover:bg-dark-primaryHover px-4 py-2 rounded text-white"
          >
            🧠 Get Started
          </button>
        </div>
      </div>

      <LoginModal isOpen={showLogin} onClose={() => setShowLogin(false)} onSuccess={handleLoginSuccess} />
    </div>
  );
}

export default Welcome;

import axios from "axios";
import { getCurrentUser, getToken } from "./login";

const API_BASE_URL = "https://farmtrak.onrender.com/api";

const getHeaders = () => ({
  "X-User-Email": getCurrentUser() || "",
  Authorization: getToken() ? `Bearer ${getToken()}` : undefined,
});

// Auth endpoints
function googleLogin(token) {
  return axios.post(`${API_BASE_URL}/google-login`, { token });
}

function sendVerification(email, username) {
  return axios.post(`${API_BASE_URL}/send-verification`, { email, username });
}

function verifyEmail(email, code) {
  console.log("Step 2: Sending POST request to /api/verify-email");
  return axios.post(`${API_BASE_URL}/verify-email`, { email, code });
}

// Flocks
function getFlocks() {
  return axios.get(`${API_BASE_URL}/flocks`, { headers: getHeaders() });
}

function saveFlock(data) {
  return axios.post(`${API_BASE_URL}/flocks`, data, { headers: getHeaders() });
}

function updateFlock(id, data) {
  return axios.put(`${API_BASE_URL}/flocks/${id}`, data, { headers: getHeaders() });
}

function deleteFlock(id) {
  return axios.delete(`${API_BASE_URL}/flocks/${id}`, { headers: getHeaders() });
}

// Expenses
function getExpenses() {
  return axios.get(`${API_BASE_URL}/expenses`, { headers: getHeaders() });
}

function saveExpense(data) {
  return axios.post(`${API_BASE_URL}/expenses`, data, { headers: getHeaders() });
}

function updateExpense(id, data) {
  return axios.put(`${API_BASE_URL}/expenses/${id}`, data, { headers: getHeaders() });
}

function deleteExpense(id) {
  return axios.delete(`${API_BASE_URL}/expenses/${id}`, { headers: getHeaders() });
}

// Eggs
function getEggProductions() {
  return axios.get(`${API_BASE_URL}/eggs`, { headers: getHeaders() });
}

function saveEggProduction(data) {
  return axios.post(`${API_BASE_URL}/eggs`, data, { headers: getHeaders() });
}

function updateEggProduction(id, data) {
  return axios.put(`${API_BASE_URL}/eggs/${id}`, data, { headers: getHeaders() });
}

function deleteEggProduction(id) {
  return axios.delete(`${API_BASE_URL}/eggs/${id}`, { headers: getHeaders() });
}

// Feed records
function getFeedRecords() {
  return axios.get(`${API_BASE_URL}/feed-records`, { headers: getHeaders() });
}

function saveFeedRecord(data) {
  return axios.post(`${API_BASE_URL}/feed-records`, data, { headers: getHeaders() });
}

function updateFeedRecord(id, data) {
  return axios.put(`${API_BASE_URL}/feed-records/${id}`, data, { headers: getHeaders() });
}

function deleteFeedRecord(id) {
  return axios.delete(`${API_BASE_URL}/feed-records/${id}`, { headers: getHeaders() });
}

// Revenue
function getRevenue() {
  return axios.get(`${API_BASE_URL}/revenue`, { headers: getHeaders() });
}

function saveRevenue(data) {
  return axios.post(`${API_BASE_URL}/revenue`, data, { headers: getHeaders() });
}

function updateRevenue(id, data) {
  return axios.put(`${API_BASE_URL}/revenue/${id}`, data, { headers: getHeaders() });
}

function deleteRevenue(id) {
  return axios.delete(`${API_BASE_URL}/revenue/${id}`, { headers: getHeaders() });
}


// ✅ Default export as one object, so existing imports work
export const apiClient = {
  googleLogin,
  sendVerification,
  verifyEmail,
  getFlocks,
  saveFlock,
  updateFlock,
  deleteFlock,
  getExpenses,
  saveExpense,
  updateExpense,
  deleteExpense,
  getEggProductions,
  saveEggProduction,
  updateEggProduction,
  deleteEggProduction,
  getFeedRecords,
  saveFeedRecord,
  updateFeedRecord,
  deleteFeedRecord,
  getRevenue,
  saveRevenue,
  updateRevenue,
  deleteRevenue,
};

// src/utils/login.js

const getEmailKey = () => {
  const userKey = Object.keys(localStorage).find(key => key.startsWith('farmtrak-user-'));
  return userKey ? localStorage.getItem(userKey) : null;
};

// Save token & user email
export const logIn = (token, email) => {
  if (!email) return;
  localStorage.setItem(`farmtrak-token-${email}`, token);
  localStorage.setItem(`farmtrak-user-${email}`, email);
};

// Clear all session-related items
export const logOut = () => {
  Object.keys(localStorage)
    .filter(key => key.startsWith('farmtrak-'))
    .forEach(key => localStorage.removeItem(key));
};

// Get the currently logged-in user's email
export const getCurrentUser = () => {
  return getEmailKey();
};

// Check if a token exists for any user
export const isLoggedIn = () => {
  return !!getToken();
};

// Get the token for the currently logged-in user
export const getToken = () => {
  const email = getEmailKey();
  return email ? localStorage.getItem(`farmtrak-token-${email}`) : null;
};

